/* ---------------------------- */

$primary-color: #7fb223;

body {
    margin: 0;
    padding: 0;
    font-family: Arial, Helvetica, sans-serif;
}

img {
    width: 100%;
}

* {
    box-sizing: border-box;
}

div,
img {
    transition: 1s;
}


/* ---------------------------- */

header {
    background: $primary-color;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    padding: 2rem;
    position: relative;
    overflow: hidden;
}

header.start {
    background-image: url("../static/header_bg_480.jpg");
    height: 30rem;
}

.logo {
    max-width: 3.5rem;
    position: absolute;
    top: 1.5rem;
    left: 1.5rem;
    transition: 0.5s ease-out;
}

.logo:hover {
    transform: scale(1.2);
}

/*nav - mobile*/

nav {
    max-height: 0;
    width: 100%;
    overflow: hidden;
    transition: 0.6s;
    text-align: center;
    opacity: 1;
    transition: 1s ease;
}

nav ul {
    list-style: none;
    margin: 0;
    padding: 0;
}

nav li {
    padding: 1rem;
    cursor: pointer;
    border-bottom: solid 1px $primary-color;
}

nav a {
    font-size: 1.5rem;
    text-decoration: none;
    font-weight: bold;
    transition: 0.5s ease;
    position: relative;
    transition: 0.5s ease;
    display: block;
}

nav a:link,
nav a:active,
nav a:visited {
    color: #000;
}

nav a:hover {
    color: $primary-color;
}

nav.show {
    max-height: 20rem;
}

.burger {
    cursor: pointer;
    background: transparent;
    border: none;
    transform: scale(1.2);
    transition: 0.5s ease-out;
    position: absolute;
    top: 2rem;
    right: 2.2rem;
}

.bar1,
.bar2,
.bar3 {
    width: 35px;
    height: 2px;
    background-color: #fff;
    margin: 9px 0;
    transition: 0.4s;
}

.burger:hover {
    transform: scale(1.5);
    transition: 0.5s ease-out;
}

.change .bar1 {
    -webkit-transform: rotate(-45deg) translate(-9px, 9px);
    transform: rotate(-45deg) translate(-8px, 9px) scale(1.2);
}

.change .bar2 {
    opacity: 0;
}

.change .bar3 {
    -webkit-transform: rotate(45deg) translate(-7px, -18px);
    transform: rotate(45deg) translate(-7px, -7px) scale(1.2);
}

:focus {
    outline: 0;
}

.pulse {
    animation: pulse 3s infinite;
}

@keyframes pulse {
    0% {
        transform: scale(1);
    }

    80% {
        transform: scale(1);
    }

    90% {
        transform: scale(1.5);
    }

    100% {
        transform: scale(1);
    }
}

.text-header {
    margin: 5rem 0 0 0;
    color: #fff;
}

.text-header ul {
    list-style: none;
    margin: 1rem 0 0;
    padding: 0;
    font-size: 1.3rem;
    max-width: 70%;
}

.text-header ul li {
    padding: 0;
    margin: 0;
}

.text-header ul li:before {
    content: "// ";
}

.text-header h1 {
    font-size: 2.5rem;
    font-family: 'Rajdhani', sans-serif;
    text-transform: uppercase;
    margin: 0;
    line-height: 2.75rem;
}

.text-header a:link,
.text-header a:active,
.text-header a:visited {
    color: #fff;
}

.profile {
    position: absolute;
    bottom: 0;
    right: -5rem;
    max-width: 18rem;
}

.some {
    font-size: 2rem;
    margin: 1.5rem 0 0 0;
}

.some i {
    cursor: pointer;
    transition: 0.5s;
    margin: 0 0.25rem 0 0;
}

.some i:hover {
    cursor: pointer;
    transform: scale(1.2);
}

// --------------------- portfolio ---------------------

header.subpage {
    background-image: url("../static/header_bg_480.jpg");
    display: flex;
    align-items: center;
    justify-content: center;
    height: 11rem;
}

.subpage-header {
    color: #fff;
    max-width: 50%;
    margin: 3rem 0 0 0;
}

.subpage-header h1 {
    font-size: 2.5rem;
    font-family: 'Rajdhani', sans-serif;
    text-transform: uppercase;
    margin: 0;
    line-height: 2.75rem;
}


// --------------------- media querty 30 rem --------------------- 

/* 30rem (480px) */

@media only screen and (min-width: 30rem) and (max-width: 47.99999rem) {

    header.start {
        background-image: url("../static/header_bg_768.jpg");
        height: 25rem;
    }

    header.subpage {
        background-image: url("../static/header_bg_768.jpg");
        height: 8rem;
    }

    .subpage-header {
        margin: 0;
    }

    .profile {
        right: -3rem;
        max-width: 18rem;
    }
}

// --------------------- media querty 48 rem --------------------- 

/* 48rem (768px) */
@media only screen and (min-width: 48rem) {

    header.start {
        height: 25rem;
        background-image: url("../static/header_bg_1080.jpg");
    }

    header.subpage {
        background-image: url("../static/header_bg_1080.jpg");
        height: 12rem;
    }

    .subpage-header {
        margin: 2rem 0 0 0;
    }

    .burger {
        display: none;
    }

    nav {
        position: absolute;
        top: 0;
        right: 0;
        width: auto;
        color: #fff;
        min-height: 5rem;
        text-align: right;
        transition: 0s;
        padding: 2rem 2rem 0 0;
        opacity: 1;
        z-index: 500;
    }

    nav li {
        display: inline;
        padding-left: 2rem;
        padding-top: 0;
        padding-right: 0;
        padding-bottom: 0;
        border-bottom: none;
    }

    nav a {
        font-size: 1.2rem;
        font-weight: normal;
        display: inline-block;
    }

    nav a:link,
    nav a:active,
    nav a:visited {
        color: #fff;
    }

    nav a:hover {
        transform: scale(1.2);
    }

    .text-header {
        max-width: 100%;
    }

    .profile {
        right: 2rem;
        max-width: 20rem;
    }
}

// --------------------- media querty 64 rem --------------------- 

/* 64rem (1024px) */
@media only screen and (min-width: 64rem) {
    header.start {
        background-image: url("../static/header_bg_1920.jpg");
        height: 25rem;
    }

    header.subpage {
        background-image: url("../static/header_bg_1920.jpg");
        height: 8rem;
    }

    .subpage-header {
        margin: 0;
    }

    .wrapper {
        max-width: 64rem;
        margin: 0 auto;
    }

    .profile {
        right: 8rem;
        max-width: 22rem;
    }
}

// --------------------- media querty 80 rem --------------------- 

/* 80rem (1280px) */
@media only screen and (min-width: 80rem) {
    .profile {
        right: 15rem;
        max-width: 25rem;
    }
}

// --------------------- media querty 100 rem --------------------- 

/* 100rem (1600px) */
@media only screen and (min-width: 100rem) {
    .profile {
        right: 23rem;
        max-width: 27rem;
    }
}

// --------------------- media querty print --------------------- 

/* Print */
@media print {
    * {
        background: transparent !important;
        color: #000 !important;
        text-shadow: none;
    }

    a,
    a:visited {
        color: #0000ff !important;
        text-decoration: underline;
    }

    a:after {
        content: " ("attr(href) ")";
    }

    abbr:after {
        content: " ("attr(title) ")";
    }

    .ir a:after {
        content: "";
    }

    /* Don't show links for images */
    pre,
    blockquote {
        border: 1px solid #999;
        page-break-inside: avoid;
    }

    img {
        page-break-inside: avoid;
    }

    @page {
        margin: 0.5cm;
    }

    p,
    h2,
    h3 {
        orphans: 3;
        widows: 3;
    }

    h2,
    h3 {
        page-break-after: avoid;
    }
}
