        grid-template-columns: 2fr 1fr;
    }
}

// --------------------- media querty 55 rem --------------------- 

/* 55rem (880px) */
@media (min-width: 55rem) {

    .portfolio .cases {
        grid-template-columns: 1fr 1fr 1fr;
    }

    .portfolio .single_cases {
        grid-template-columns: 2fr 1fr;
    }
}

// --------------------- media querty 64 rem --------------------- 

/* 64rem (1024px) */
@media (min-width: 64rem) {
    header.start {
        background-image: url("../static/header_bg_1920.jpg");
        height: 25rem;
    }

    header.subpage {
        background-image: url("../static/header_bg_1920.jpg");
        height: 9rem;
    }

    .subpage-header {
        margin: 0;
    }

    .wrapper {
        max-width: 64rem;
        margin: 0 auto;
    }

    .profile {
        right: 8rem;
        max-width: 22rem;
    }

    .portfolio .cases {
        grid-template-columns: 1fr 1fr 1fr;
    }

    .portfolio .single_cases {
        grid-template-columns: 2fr 1fr;
    }
}

// --------------------- media querty 80 rem --------------------- 

/* 80rem (1280px) */
@media (min-width: 80rem) {
    .profile {
        right: 15rem;
        max-width: 25rem;

    }

    .details_wrapper {
        padding: 5rem 0 0 0;
    }
}


// --------------------- media querty 100 rem --------------------- 

/* 100rem (1600px) */
@media (min-width: 100rem) {
    .profile {
        right: 23rem;
        max-width: 27rem;
    }

    .display_wrapper {
        padding: 2rem 0 0 0;
    }

    .details_wrapper {
        padding: 7rem 0 0 0;
    }
}

// --------------------- media querty print --------------------- 

/* Print */
@media print {
    * {
        background: transparent !important;
        color: #000 !important;
        text-shadow: none;
    }

    a,
    a:visited {
        color: #0000ff !important;
        text-decoration: underline;
    }

    a:after {
        content: " ("attr(href) ")";
    }

    abbr:after {
        content: " ("attr(title) ")";
    }

    .ir a:after {
        content: "";
    }

    /* Don't show links for images */
    pre,
    blockquote {
        border: 1px solid #999;
        page-break-inside: avoid;
    }

    img {
        page-break-inside: avoid;
    }

    @page {
        margin: 0.5cm;
    }

    p,
    h2,
    h3 {
        orphans: 3;
        widows: 3;
    }

    h2,
    h3 {
        page-break-after: avoid;
    }
}
